import React, { useState } from 'react';
import './users.css';

function Users() {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [role, setRole] = useState('');

  const addUser = (event) => {
    event.preventDefault();

    const newUser = {
      username,
      email,
      phone,
      role
    };

    setUsers([...users, newUser]);
    setShowForm(false);
    resetFormInputs();
  };

  const toggleForm = () => {
    setShowForm(!showForm);
    resetFormInputs();
  };

  const resetFormInputs = () => {
    setUsername('');
    setEmail('');
    setPhone('');
    setRole('');
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setUsername(user.username);
    setEmail(user.email);
    setPhone(user.phone);
    setRole(user.role);
    setShowForm(true);
  };

  const handleUpdateUser = (event) => {
    event.preventDefault();

    const updatedUser = {
      ...editingUser,
      username,
      email,
      phone,
      role
    };

    const updatedUsers = users.map((user) =>
      user.username === editingUser.username ? updatedUser : user
    );

    setUsers(updatedUsers);
    setEditingUser(null);
    setShowForm(false);
    resetFormInputs();
  };

  const goBack = () => {
    setEditingUser(null);
    setShowForm(false);
    resetFormInputs();
  };

  return (
    <div className="user-form2">
      <h1 className="heading">Users</h1>
      {showForm ? (
        <form className="form-container1" onSubmit={editingUser ? handleUpdateUser : addUser}>
          {/* Form fields */}
          {/* ... */}

          <div className="form-buttons-container1">
            <button className="form-submit-button1">{editingUser ? 'Update User' : 'Add User'}</button>
            <button className="back-button" onClick={goBack}>Back</button>
          </div>
        </form>
      ) : (
        <div className="user-form2">
          <div className="columns-container">
            {/* Table content */}
            <table>
              <thead>
                <tr>
                  <th colSpan="4">User Details</th>
                  <th>
                    <button onClick={toggleForm}>Add a User</button>
                  </th>
                </tr>
                <tr>
                  <th>Username</th>
                  <th>Email</th>
                  <th>Phone No.</th>
                  <th>Role</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user.username}>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>{user.phone}</td>
                    <td>{user.role}</td>
                    <td>
                      <button onClick={() => handleEditUser(user)}>Edit</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default Users;
