import React, { useState } from 'react';
import './users.css';

function Users() {
  const [users, setUsers] = useState([]);

  const addUser = () => {
    const newUser = {
      id: users.length + 1,
      name: `User ${users.length + 1}`,
      email: `user${users.length + 1}@example.com`,
      phone: '123-456-7890',
      address: '123 Main St',
    };

    setUsers([...users, newUser]);
  };

  return (
    <div className="App">
      <h1 style={{ color: 'red' }}>Users</h1>
      <table>
        <thead>
          <tr>
            <th colSpan="4">User Details</th>
            <th>
              <button onClick={addUser}>Add a User</button>
            </th>
          </tr>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.address}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Users;
