import React, { useState } from 'react';
import './users.css';

function Users() {
  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editUser, setEditUser] = useState(null);

  const addUser = (event) => {
    event.preventDefault();
    const form = event.target;
    const username = form.elements.username.value;
    const password = form.elements.password.value;
    const email = form.elements.email.value;
    const phone = form.elements.phone.value;
    const role = form.elements.role.value;

    const newUser = {
      username: username,
      password: password,
      email: email,
      phone: phone,
      role: role,
      action: "",
    };

    setUsers([...users, newUser]);
    setShowForm(false);

    // Reset form input values
    form.reset();
  };

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  const goBack = () => {
    setShowForm(false);
  };

  const handleEditUser = (user) => {
    setEditUser(user);
  };

  const handleSaveUser = (editedUser) => {
    setUsers((prevUsers) =>
      prevUsers.map((user) => {
        if (user === editUser) {
          return {
            ...user,
            ...editedUser,
          };
        }
        return user;
      })
    );

    setEditUser(null);
  };

  return (
    <div className="user-form2">
      <h1 className="heading">Users</h1>
      {showForm ? (
        <form className="form-container1" onSubmit={addUser}>
          <div className="columns-container1">
            <div className="column1">
              <div className="row">
                <label htmlFor="username">
                  Username<span className="requiredfield">*</span>
                </label>
              </div>
              <div className="row">
                <input type="text" id="username" required name="username" placeholder="Username" />
              </div>
              <div className="row">
                <label htmlFor="pass">
                  Password<span className="required-field">*</span>
                </label>
              </div>
              <div className="row">
                <input type="password" id="pass" required name="password" placeholder="Password" />
              </div>
              <div className="row">
                <label htmlFor="phn">Phone No.<span className="required-field">*</span></label>
              </div>
              <div className="row">
                <input type="number" id="phn" required name="phone" placeholder="Phone no" />
              </div>
            </div>
            <div className="column1">
              <div className="row">
                <label htmlFor="mail">Email<span className="required-field">*</span></label>
              </div>
              <div className="row">
                <input type="email" id="mail" name="email" required placeholder="Email" />
              </div>
              <div className="row">
                <label htmlFor="passw">
                  Confirm Password<span className="required-field">*</span>
                </label>
              </div>
              <div className="row">
                <input type="password" id="passw" required placeholder="Confirm Password" />
              </div>
              <div className="row">
                <label htmlFor="role">Role<span className="required-field">*</span></label>
              </div>
              <div className="row">
                <input type="text" id="role" name="role" required placeholder="Super Admin" />
              </div>
            </div>
          </div>
          <div className="form-buttons-container1">
            <button className="form-submit-button1">Add User</button>
            <button className="back-button" onClick={goBack}>Back</button>
          </div>
        </form>
      ) : (
        <div className="user-form2">
          <div className="columns-container">
            <table>
            <table>
              <thead>
                <tr>
                <th colSpan="4">User Details</th>
                <th>
                  <button onClick={toggleForm}>Add a User</button>
                </th>
                </tr>
                <tr>
                  <th>Username</th>
                  <th>Email</th>
                  <th>Phone No.</th>
                  <th>Role</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user.username}>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>{user.phone}</td>
                    <td>{user.role}</td>
                    <td>
                      <button onClick={() => setEditUser(user)}>Edit</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            </table>
          </div>
        </div>
      )}
      {editUser && (
        <EditForm user={editUser} onSave={handleSaveUser} onCancel={() => setEditUser(null)} />
      )}
    </div>
  );
}

function EditForm({ user, onSave, onCancel }) {
  const [editedUser, setEditedUser] = useState({
    username: user.username,
    email: user.email,
    phone: user.phone,
    role: user.role,
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEditedUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSave(editedUser);
  };

  return (
    <div className="edit-form-container">
      <h2>Edit User</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          name="username"
          value={editedUser.username}
          onChange={handleInputChange}
          required
        />

        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={editedUser.email}
          onChange={handleInputChange}
          required
        />

        <label htmlFor="phone">Phone No.:</label>
        <input
          type="number"
          id="phone"
          name="phone"
          value={editedUser.phone}
          onChange={handleInputChange}
          required
        />

        <label htmlFor="role">Role:</label>
        <input
          type="text"
          id="role"
          name="role"
          value={editedUser.role}
          onChange={handleInputChange}
          required
        />

        <div className="form-buttons-container1">
          <button type="submit">Save</button>
          <button type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default Users;
