import React, { useState } from 'react';
import { Link, NavLink, Outlet, useNavigate } from 'react-router-dom';
import '../components/navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const [isMenuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    navigate('/'); // Redirect to the Loginscreen component
  };

  const handleMenuToggle = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <div>
      <nav className="navbar">
        <div className="logo">
          <img src={`${process.env.PUBLIC_URL}/assets/LoginTemple.jpg`} alt="Logo" />
        </div>

        <ul className={`navbar-menu${isMenuOpen ? ' open' : ''}`}>
          <li>
            <NavLink to="bhajanamindralu">Bhajana Mandiralu</NavLink>
          </li>
          <li>
            <NavLink to="newtempleconstruction">New Temple Construction</NavLink>
          </li>
          <li>
            <NavLink to="renovation">Renovation of Temple</NavLink>
          </li>
          <li>
            <NavLink to="users">Users</NavLink>
          </li>
          <li>
            <NavLink to="reports">Reports</NavLink>
          </li>
          <li className="logout" onClick={handleLogout}>Logout</li>
        </ul>
      </nav>

      {/* Dropdown Menu */}
      {isMenuOpen && (
        <div className="dropdown-menu">
          <ul>
            <li>
              <NavLink to="bhajanamindralu">Bhajana Mandiralu</NavLink>
            </li>
            <li>
              <NavLink to="newtempleconstruction">New Temple Construction</NavLink>
            </li>
            <li>
              <NavLink to="renovation">Renovation of Temple</NavLink>
            </li>
            <li>
              <NavLink to="users">Users</NavLink>
            </li>
            <li>
              <NavLink to="reports">Reports</NavLink>
            </li>
            <li className="logout" onClick={handleLogout}>Logout</li>
          </ul>
        </div>
      )}

      <Outlet />
    </div>
  );
};

export default Navbar;
