import React, { useState } from 'react';
import { Link, NavLink, Outlet, useNavigate } from 'react-router-dom';
import '../components/navbar.css';

const Navbar = ({ setActivePage }) => {
  const navigate = useNavigate();
  const [isMenuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    navigate('/'); // Redirect to the Loginscreen component
  };

  const handleClick = (page) => {
    setActivePage(page);
  };

  const handleLogoClick = () => {
    setActivePage('');
  };

  const handleMenuToggle = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="navbar">
      <div className="logo" onClick={handleLogoClick}>
        <img src={`${process.env.PUBLIC_URL}/assets/LoginTemple.jpg`} alt="Logo" />
      </div>
      <div className={`menu-toggle${isMenuOpen ? ' open' : ''}`} onClick={handleMenuToggle}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>
      <ul className={`navbar-menu${isMenuOpen ? ' open' : ''}`}>
        <li>
          <NavLink to="bhajanamindralu" onClick={() => handleClick('bhajanamindralu')}>
            Bhajana Mandiralu
          </NavLink>
        </li>
        <li>
          <NavLink to="newtempleconstruction" onClick={() => handleClick('reports')}>
            New Temple Construction
          </NavLink>
        </li>
        <li>
          <NavLink to="renovation" onClick={() => handleClick('reports')}>
            Renovation of Temple
          </NavLink>
        </li>
        <li>
          <NavLink to="users" onClick={() => handleClick('users')}>
            Users
          </NavLink>
        </li>
        <li>
          <NavLink to="reports" onClick={() => handleClick('reports')}>
            Reports
          </NavLink>
        </li>
        <li className="logout" onClick={handleLogout}>
          Logout
        </li>
      </ul>
      <Outlet />
    </nav>
  );
};

export default Navbar;
