import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from './navbar';
import Bhajanamindralu from '../components/bhajanmindralu';
import Users from '../components/users';
import Reports from '../components/reports';
import Home from '../components/home';
import axios from 'axios';
import './landing.css';

const Landing = () => {
  const [activePage, setActivePage] = useState('');
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const storedPage = localStorage.getItem('activePage');
    if (storedPage) {
      setActivePage(storedPage);
      if (
        storedPage === 'bhajanamindralu' ||
        storedPage === 'users' ||
        storedPage === 'reports'
      ) {
        window.addEventListener('beforeunload', handleBeforeUnload);
      }
    }
  }, []);

  useEffect(() => {
    if (!location.state?.fromReload) {
      localStorage.setItem('activePage', activePage);
    }
  }, [activePage, location]);

  useEffect(() => {
    const handleSessionExpired = () => {
      alert('Your session has expired. Please login again.');
      navigate('/');
    };

    const checkTokenExpiration = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const response = await axios.get('/protected', {
            headers: { Authorization: `Bearer ${token}` },
          });
          console.log(response.data); // Access granted, token is valid
        } catch (error) {
          console.error(error.response);
          if (error.response && error.response.status === 401) {
            handleSessionExpired();
          }
        }
      } else {
        handleSessionExpired();
      }
    };

    checkTokenExpiration();

    // Cleanup: Remove event listener
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  const handleSetActivePage = (page) => {
    setActivePage(page);
    navigate(`/Landing/${page}`);
  };

  const handleBeforeUnload = (event) => {
    event.preventDefault();
    event.returnValue = '';
  };

  const renderPageContent = () => {
    switch (activePage) {
      case 'Home':
        return <Home />;
      case 'bhajanamindralu':
        return <Bhajanamindralu />;
      case 'users':
        return <Users />;
      case 'reports':
        return <Reports />;
      default:
        return null;
    }
  };

  return (
    <div>
      <Navbar setActivePage={handleSetActivePage} />
      <div className="container">
        <div className="imagecontainer1"></div>
        <div className="imagecontainer1"></div>
        <div className="page-content">{renderPageContent()}</div>
      </div>
    </div>
  );
};

export default Landing;
