import React, { useState, useEffect } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import Login from './components/loginscreen.js';
import Landing from './components/Landing';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const previouslyOpenedPage = sessionStorage.getItem('previouslyOpenedPage');
    sessionStorage.removeItem('previouslyOpenedPage');

    if (previouslyOpenedPage && previouslyOpenedPage !== location.pathname) {
      alert('All entries will be cleared.');
    }
  }, [location]);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  useEffect(() => {
    sessionStorage.setItem('previouslyOpenedPage', location.pathname);
  }, [location]);

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<Login setLoggedIn={setLoggedIn} handleLogin={handleLogin} />} />
        <Route path="/Landing/*" element={<Landing />} />
      </Routes>
    </div>
  );
};

export default App;
